dict.string:
get_pw () { res=$(CocoaDialog 2>/dev/null secure-inputbox --float --no-newline \ --title 'Decrypt AES 128 Encrypted Text' \ --informative-text "$1" \ --button1 "$2" --button2 Cancel) [[ ${res:0:1} == 1 ]] && echo -n "${res:2}" }
pw=$(get_pw 'What is the password?' Decrypt) [[ -z "$pw" ]] && exit_discard
if ! openssl enc -d -aes128 -base64 -pass "pass:$pw"; then exit_show_tool_tip fi
