#!/usr/bin/env ruby begin require 'rubygems' require 'redcloth' rescue LoadError require "#{ENV['TM_SUPPORT_PATH']/lib/redcloth" end
require "#{ENV['TM_SUPPORT_PATH']}/lib/tm/htmloutput"
contents = [] $stdin.each_line { |line| contents << line }
output = RedCloth.new(contents.join()).to_html(:textile)
TextMate::HTMLOutput.show(:title => "Textile Preview", :sub_title => ENV['TM_FILENAME']) do |io| io << output end
